import React from "react";

const languages = [
  "All",
  "Python",
  "Go",
  "Java",
  "TypeScript",
  "JavaScript",
  "Rust",
  "C++",
  "C",
  "C#",
];

const filterTags = [
  "Bug",
  "Enhancement", 
  "Good First Issue", 
  "Help Wanted", 
  "Documentation",
];



function Sidebar({ selectedLanguage = "All", onSelectLanguage, sortStars, sortUpdated, onSortStarsChange, onSortUpdatedChange }) {
  const handleLanguageSelect = (lang) => {
    onSelectLanguage(lang === "All" ? "All" : lang);
  };

  return (
    <aside className="w-72 p-8 bg-gray-900 text-gray-200 shadow-lg">
      <h2 className="text-2xl font-bold mb-6">ABOUT</h2>
      <p className="mb-8 text-sm">
        Good First Issue curates easy pickings from popular open-source projects
        and helps you make your first contribution to open-source.
      </p>
      <h3 className="text-xl font-semibold mb-4">BROWSE BY LANGUAGE</h3>
      <div className="flex flex-wrap gap-3 mb-8">
        {languages.map((lang) => (
          <button
            key={lang}
            onClick={() => handleLanguageSelect(lang)}
            className={`px-4 py-2 rounded-lg text-sm font-medium ${
              selectedLanguage === lang || (lang === "All" && selectedLanguage === "All")
                ? "bg-green-600 text-white shadow-md"
                : "bg-gray-700 hover:bg-gray-600"
            } transition duration-200`}
          >
            {lang}
          </button>
        ))}
      </div>
      <h3 className="text-xl font-semibold mb-4">FILTER BY TAG</h3>
      <div className="flex flex-wrap gap-2 mb-8">
        {filterTags.map((tag) => (
          <label key={tag} className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={selectedTags.includes(tag)}
              onChange={() => onToggleTag(tag)}
              className="form-checkbox"
            />
            <span>{tag}</span>
          </label>
        ))}
      </div>
      <div className="mb-8">
        <h3 className="text-xl font-semibold mb-4">SORT OPTIONS</h3>
        <div className="mb-4">
          <label className="block mb-2">Sort by stars:</label>
          <select value={sortStars} onChange={(e) => onSortStarsChange(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white">
            <option value="desc">Descending</option>
            <option value="asc">Ascending</option>
          </select>
        </div>
        <div className="mb-4">
          <label className="block mb-2">Sort by last updated:</label>
          <select value={sortUpdated} onChange={(e) => onSortUpdatedChange(e.target.value)} className="w-full p-2 rounded bg-gray-700 text-white">
            <option value="recent">Most Recent</option>
            <option value="oldest">Oldest</option>
          </select>
        </div>
      </div>
      <button className="w-full py-3 bg-green-600 rounded-lg font-semibold text-white hover:bg-green-700 transition duration-200">
        ADD YOUR PROJECT
      </button>
    </aside>
  );
}

export default Sidebar;
