import React, { useState } from 'react';
import { FaRegComments } from 'react-icons/fa6';

function ProjectCard({ project, isSelected, onSelect }) {
  const [hoveredIssueId, setHoveredIssueId] = useState(null);
  const [showAllIssues, setShowAllIssues] = useState(false);

  // Check if issues array exists and has a length property
  const issuesCount = project.issues_count;

  const getTopLanguages = (languages) => {
    const languageEntries = Object.entries(languages);
    const sortedLanguages = languageEntries.sort((a, b) => b[1] - a[1]);
    const top3Languages = sortedLanguages.slice(0, 3);
    return top3Languages;
  };

  const formatDate = (isoDate) => {
    const date = new Date(isoDate);
    return date.toLocaleString('en-US', {
      month: 'long',
      day: 'numeric',
      year: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true,
    });
  };

  // Extract languages from the object
  const languages = project.languages || {};
  const languageEntries = getTopLanguages(languages);

  const formatStars = (stars) => {
    if (stars === undefined || stars === null) {
      return 'N/A';
    }
    if (typeof stars !== 'number') {
      return stars.toString();
    }
    if (stars >= 1000) {
      return (stars / 1000).toFixed(2) + 'k';
    }
    return stars.toString();
  };

  const primaryLanguage = languageEntries.length > 0 ? languageEntries[0][0] : 'Unknown';

  const handleIssueClick = (event, url) => {
    event.stopPropagation();
    window.open(url, '_blank'); 
  };

  // Helper function to truncate text
  const truncateText = (text = '', maxLength) => {
    if (text.length > maxLength) {
      return text.substring(0, maxLength) + '...';
    }
    return text;
  };

  return (
    <div
      className={`bg-gray-800 rounded-lg p-4 transition-all duration-300 ease-in-out
        ${isSelected ? "ring-2 ring-green-500" : "hover:bg-gray-750"} w-full`}
      onClick={onSelect}
    >
      <div className="flex justify-between items-start mb-2">
        <h3 className={`text-xl font-semibold ${isSelected ? "text-green-500" : ""}`}>
          {truncateText(project.name, 40)}  {/* Truncate project name to 20 characters */}
        </h3>
        <span
          className={`px-2 py-1 rounded-full border border-green-500 text-sm font-bold ${isSelected ? "text-green-500" : "text-white"}`}
        >
          {issuesCount} {issuesCount === 1 ? "issue" : "issues"}
        </span>
      </div>
      <p className="text-gray-400 mb-4">{truncateText(project.description, 120)}</p> {/* Truncate description to 100 characters */}
      <div className={`flex items-center text-sm ${isSelected ? "text-green-500" : "text-gray-500"}`}>
        <span className="mr-4">
          stars: <span className="text-yellow-500">{formatStars(project.stars)}</span>
        </span>
        <span>
          last activity:{" "}
          <span className="text-yellow-500">{formatDate(project.lastActivity)}</span>
        </span>
      </div>

      {languageEntries.length > 0 && (
        <div className="mt-2 text-sm">
          <span className="text-sm text-gray-500">
            <ul className="flex flex-wrap mt-1">
              {languageEntries.map(([language]) => (
                <li
                  key={language}
                  className="bg-blue-500 text-white text-xs font-semibold rounded-full px-2 py-1 mr-2 mb-1"
                >
                  {language}
                </li>
              ))}
            </ul>
          </span>
        </div>
      )}

      {isSelected && project.issues && (
        <div className="mt-2 space-y-1">
          <h4 className="text-lg font-semibold">Issues:</h4>
          {project.issues.slice(0, showAllIssues ? project.issues.length : 5).map((issue) => (
            <div
              key={issue.url}
              className={`p-2 rounded flex justify-between items-center transition-colors duration-200 cursor-pointer
                ${hoveredIssueId === issue.url ? "text-green-500" : ""}`}
              onMouseEnter={() => setHoveredIssueId(issue.url)}
              onMouseLeave={() => setHoveredIssueId(null)}
              onClick={(event) => handleIssueClick(event, issue.url)}
            >
              <div>
                <h5 className="font-medium text-sm">{truncateText(issue.title, 30)}</h5> {/* Truncate issue title to 30 characters */}
                <div className="flex flex-wrap space-x-2 mt-1">
                  {issue.labels.map((label) => (
                    <span key={label} className="text-xs bg-gray-700 text-white px-2 py-1 rounded-full">
                      {label}
                    </span>
                  ))}
                </div>
              </div>
              <span className="text-white p-2 rounded-full flex items-center">
                <FaRegComments className="ml-1 text-white m-1" />
                {issue.commentCount}
              </span>
            </div>
          ))}
          {!showAllIssues && project.issues.length > 5 && (
            <button
              onClick={() => setShowAllIssues(true)}
              className="mt-2 text-green-500 hover:underline"
            >
              Show more issues
            </button>
          )}
        </div>
      )}
    </div>
  );
}

export default ProjectCard;
