import React, { useState } from 'react';
import { FaRegComments } from 'react-icons/fa6';

function ProjectCard({ project, isSelected, onSelect }) {
  const [hoveredIssue, setHoveredIssue] = useState(null);

  // Check if issues array exists and has a length property
  const issuesCount = project.issues ? project.issues.length : 0;

  // Extract and sort languages
  const topLanguages = project.languages;

  return (
    <div
      className={`bg-gray-800 rounded-lg p-4 transition-all duration-300 ease-in-out
        ${isSelected ? "ring-2 ring-green-500" : "hover:bg-gray-750"} w-full`}
      onClick={onSelect}
    >
      <div className="flex justify-between items-start mb-2">
        <h3
          className={`text-xl font-semibold ${
            isSelected ? "text-green-500" : ""
          }`}
        >
          {project.name}
        </h3>
        <span
          className={`px-2 py-1 rounded-full border border-green-500 text-sm font-bold ${
            isSelected ? "text-green-500" : "text-white"
          }`}
        >
          {issuesCount} {issuesCount === 1 ? "issue" : "issues"}
        </span>
      </div>
      <p className="text-gray-400 mb-4">{project.description}</p>
      <div
        className={`flex items-center text-sm ${
          isSelected ? "text-green-500" : "text-gray-500"
        }`}
      >
        <span className="mr-4">
          stars: <span className="text-yellow-500">{project.stars}</span>
        </span>
        <span>
          last activity:{" "}
          <span className="text-yellow-500">{project.lastActivity}</span>
        </span>
      </div>

      {topLanguages.length > 0 && (
        <div className="mt-2 text-sm">
          <span
            className={`text-sm ${
              isSelected ? "text-green-500" : "text-gray-500"
            }`}
          >
            Top Languages:
          </span>
          <ul className="flex flex-wrap">
            {topLanguages.map(([language, bytes]) => (
              <li
                key={language}
                className="bg-blue-500 text-white text-xs font-semibold rounded-full px-2 py-1 mr-2 mb-1"
              >
                {language} ({bytes.toLocaleString()})
              </li>
            ))}
          </ul>
        </div>
      )}

      {isSelected && project.issues && (
        <div className="mt-2 space-y-1">
          <h4 className="text-lg font-semibold">Issues:</h4>
          {project.issues.map((issue) => (
            <div
              key={issue.id}
              className={`p-2 rounded flex justify-between items-center transition-colors duration-200
                ${
                  hoveredIssue === issue.id ? "bg-green-500 bg-opacity-20" : ""
                }`}
              onMouseEnter={() => setHoveredIssue(issue.id)}
              onMouseLeave={() => setHoveredIssue(null)}
            >
              <h5 className="font-medium text-sm">{issue.title}</h5>
              <span className="text-white p-2 rounded-full flex items-center">
                <FaRegComments className="ml-1 text-white m-1" />
                {issue.commentCount}
              </span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ProjectCard;
