import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard';
import axios from 'axios';

function ProjectList({ selectedLanguage, selectedTags }) {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:4000/repos');
        setProjects(response.data);
      } catch (err) {
        setError('Failed to fetch projects.');
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const filteredProjects = selectedLanguage === "All"
    ? projects
    : projects.filter(project => {
        const projectLanguages = Object.keys(project.languages || {});
        return projectLanguages.includes(selectedLanguage);
      });

  return (
    <div className="p-6 w-full">
      <div className="space-y-6">
        {filteredProjects.map((project) => (
          <ProjectCard
            key={project.name}
            project={project}
            isSelected={selectedProject === project.name}
            onSelect={() => setSelectedProject(project.name)}
            selectedTags={selectedTags}
          />
        ))}
      </div>
    </div>
  );
}

export default ProjectList;
