import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import ProjectList from "./components/ProjectList";

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState('All');
  const [sortStars, setSortStars] = useState('desc');
  const [sortUpdated, setSortUpdated] = useState('recent');
  const [selectedTags, setSelectedTags] = useState([]);

  const handleToggleTag = (tag) => {
    setSelectedTags(prevTags =>
      prevTags.includes(tag) ? prevTags.filter(t => t !== tag) : [...prevTags, tag]
    );
  };

  return (
    <div className="flex flex-col md:flex-row bg-gray-900 text-white min-h-screen justify-center">
      <div className="flex flex-col md:flex-row max-w-7xl w-full mx-auto">
        <Sidebar 
          selectedLanguage={selectedLanguage}
          onSelectLanguage={setSelectedLanguage}
          sortStars={sortStars}
          sortUpdated={sortUpdated}
          onSortStarsChange={setSortStars}
          onSortUpdatedChange={setSortUpdated}
          selectedTags={selectedTags}
          onToggleTag={handleToggleTag}
        />
        <main className="flex-1 p-8">
          <h1 className="text-3xl font-bold mb-8 text-center">
            Github Glimpse
          </h1>
          <ProjectList 
            selectedLanguage={selectedLanguage} 
            sortStars={sortStars}
            sortUpdated={sortUpdated}
            selectedTags={selectedTags}
          />
        </main>
      </div>
    </div>
  );
}

export default App;
