import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard';
import axios from 'axios';

function ProjectList({ selectedLanguage }) {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:4000/repos'); // Adjust API endpoint as needed
        setProjects(response.data);
      } catch (err) {
        setError('Failed to fetch projects.');
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const filteredProjects = projects.filter(
    (project) => !selectedLanguage || project.language === selectedLanguage
  );

  const handleSelect = (projectName) => {
    setSelectedProject((prevSelected) =>
      prevSelected === projectName ? null : projectName
    );
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="p-6 w-full">
      <div className="space-y-6">
        {filteredProjects.map((project) => (
          <ProjectCard
            key={project.name}
            project={project}
            isSelected={selectedProject === project.name}
            onSelect={() => handleSelect(project.name)}
          />
        ))}
      </div>
    </div>
  );
}

export default ProjectList;
